INSERT INTO `teacher` VALUES ('1', '', '/Excellent/pages/images/fanyong.jpg', '范  勇 ', '范  勇', '教授，博士，硕士生导师，学术带头人，四川省计算机学会多媒体专委员会委员。主要研究方向为机器视觉、图像理解和软件测试技术。在研项目“强激光光学元件表面疵病检测技术研究”、“基于机器视觉的精密表面疵病检测系统”等，获得软件著作权3项，获发明专利3项。主要承担《软件项目管理》、《软件需求》等 课程教学工作。', '1000', '教授', '论牟泽波的重要性', null, '神级选手', '做审计选手，你也行');
INSERT INTO `teacher` VALUES ('2', null, '/Excellent/pages/images/liyufeng.jpg', '李郁峰', '李郁峰', '副教授，博士，硕士生导师，计算机科学与技术学院实验教学中心主任。主要研究方向为多传感器图像融合、自动目 标识别技术。作为主要研发人员承担了包括国家自然科学基金和四川省教育厅基金等在内的5项课题；在国内外核心期刊上发表论文10余篇，EI检索2篇。国家发明专利2项，软件著作权2项。主要 承担《C++程序设计语言》等课程教学工作。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('3', null, '/Excellent/pages/images/panya.jpg', '潘  娅', '潘  娅', '副教授，硕士生导师，计算机科学与技术学院软件工程系系主任，中国计算机学会容错计算专委会委员，四川省精品课程《软件测试技术》主讲教师。主要研究方向为机器人视觉感知、软件测试技术。承担多项部省科研课题，申请国家发明专利1项。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('4', null, '/Excellent/pages/images/chenniannian.jpg', '陈念年', '陈念年', '副教授，博士，硕士生导师，计算机科学与技术学院实验教学中心主任。主要研究方向为多传感器图像融合、自动目 标识别技术。作为主要研发人员承担了包括国家自然科学基金和四川省教育厅基金等在内的5项课题；在国内外核心期刊上发表论文10余篇，EI检索2篇。国家发明专利2项，软件著作权2项。主要 承担《C++程序设计语言》等课程教学工作。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('5', null, '/Excellent/pages/images/gaolin.jpg', '高  琳', '高  琳', '博士。主要研究方向为：数字图像处理、模式识别和机器视觉。近年来，在国内外核心期刊上发表论文近20篇，其中SCI检索1篇、EI检索12篇；参与完成各级纵向和横向科技项目10余项目；申请国家发明专利1项。主要承担《离散数学》等课程教学工作。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('6', null, '/Excellent/pages/images/yangchunming.jpg', '杨春明', '杨春明', '知识工程与教育技术教学科研团队负责人，\r\nACM集训队指导教师。主要研究方向为数据\r\n挖掘，自然语言处理，智能信息处理，主讲《算法分析与设计》课程。承担“核安全事件网络舆情检测与预警关键技术研究”、“三网融合下内容监管关键技术研究”等多项省部级及企业委托项目，获四川省科技进步三等奖1项，专利及软件著作权8项。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('7', null, '/Excellent/pages/images/chenyonghui.jpg', '陈永辉', '陈永辉', '副教授，中国计算机学会会员，软件工程系副主任。从事科学计算可视化方面的研究，主持并参与了国家863、自然科学基金、教育厅等多项重大项目、企事业的横向项目，获得四川省科技进步奖三等奖一项多次指导学生获得国家级、省级科技活动及比赛奖项。校级优秀教师。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('8', null, '/Excellent/pages/images/huangxiaofang.jpg', '黄晓芳', '黄晓芳', '副教授，博士，主要研究方向为数字内容版权管理、认证机制的研究以及信息系统安全评估。主持国家自然基金、四川省科技厅、教育厅基金、校博士基金以及多项横向项目等项目，在一级学报及国际会议发表多篇论文，其中EI检索5篇。主要承担《信息安全导论》、《网络攻防对抗》、《网络安全与实 践》等课程教学。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('9', null, '/Excellent/pages/images/zhangweidong.jpg', '张卫东', '张卫东', '副教授，在国内外期刊发表论文15篇，其中EI收录、一级核心、核心5篇。参与国家863项目、省科技厅等国家级、省部级项目多项。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('10', null, '/Excellent/pages/images/fantaihua.jpg', '范太华', '范太华', '副教授，硕士生导师。主要从事数据与知识工程、物联网技术、教育软件设计等领域的研究与开发工作。先后主持或主研完成省部级科研项目三项，市、厅级科研项两项目，主持完成多个大中型企事业单位的信息系统开发设计。目前主持在研项目有四川省科技支撑项目《RFID多标签识别关键技术研究》、企业委托科研项目《XX水泥厂智能设备管理系统》等项目两项。研究成果获厅级科技成果三等奖一项，发表相关科研论文近20篇，获专利一项。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('11', null, '/Excellent/pages/images/lihuizhuo.jpg', '李绘卓', '李绘卓', '资深C++Windows程序设计师，四川省精品课程《软件测试技术》主讲教师。主要研究方向为：图像处理、机器视觉和多源图像融合。主持横向项目3项，参与国家自然科学基金、国家863、中国电子科技集团公司等各类项目6项。主要承担《程序综合设计》、《软件测试技术》等课程的教学工作。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('12', null, '/Excellent/pages/images/liuchang.jpg', '刘   畅', '刘   畅', '主要研究方向为：机器视觉和数字图像处理。主持校级青年基金项目1项，参与重庆44所多路CCD图像校正等科研项目3项；在国内外核心期刊上发表学术论文5篇。主要承担《计算机图形学》、《面向对象程序设计（C++）》等课程的教学工作，主持校级教学改革项目1项。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('14', null, '/Excellent/pages/images/liuyong.jpg', '刘   涌', '刘   涌', '博士，副教授。主要研究方向为计算机立体视觉、三维重建技术、医疗影像处理等。主持四川省教育厅项目2项，承担国家级、省部级科研项目多项，发表科研论文10余篇。主讲《面向对象程序设计（Java）》、《医疗影像三维重建技术》课程。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('15', null, '/Excellent/pages/images/gaoxiaoming.png', '高小明', '高小明', '讲师，硕士：主要研究嵌入式系统技术方面，在RFID射频识别和医疗仪器方面，具有较丰富的开发经验，参与了省科技厅、教育厅、校级多项科研项目的开发，与企业联合进行了多项嵌入式产品项目的开发，部分产品达到国内领先水平，远销欧美等国家。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('16', null, '/Excellent/pages/images/zenglisheng.png', '曾立胜', '曾立胜', '男，硕士，副教授。主要研究方向有软件算法设计、数据库与知识工程。获3次学校“优秀教学质量奖”和1次学校“优秀教师”称号，发表科研论文10余篇。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('17', null, '/Excellent/pages/images/lixuejun.png', '李学俊', '李学俊', '男，博士，副教授；1975年1月10日生于四川省资阳市，中国共产党党员。主要从事信息系统分析与设计、数据库、多源图像处理及融合技术及其应用的研究。近几年发表“基于地图切片预取的WebGIS系统”等论文7篇，EI检索数篇；主持了“气动工程软件研发”等横向项目数项和“基于人工智能的主观题自动评分算法研究与实现”等校级项目3项，参与省级以上科研项目数项。承担“数据结构”，“算法分析与设计”等课程的教学工作。(E_mail: lixuejun@swust.edu.cn)', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('18', null, '/Excellent/pages/images/zhangkefei.png', '张克非', '张克非', '副教授，主要从事无线网络技术，分布式计算研究。近几年发表论文10余篇，重要核心5篇，ISTP检索1篇；发表著作1本，教材2本；主持教育厅项目1项。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('19', null, '/Excellent/pages/images/yangcheng.png', '杨  程', '杨  程', '硕士研究生，助理研究员，资深C++/Windows程序设计师。主要研究方向：3D仿真，科学计算可视化，数字图像处理。主持校级青年预研基金项目1项，参与国家自然科学基金、中国工程物理研究院及中国空气动力研究与发展中心委托研制等项目6项；发表论文8篇。承担《软件过程》、《Windows程序设计》等课程教学，获学院优秀科技教师奖，优秀毕业设计指导教师等称号。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('20', null, '/Excellent/pages/images/jiaxiaolin.png', '贾小林', '贾小林', '博士，副教授，计算机工程系主任，IEEE会员，ACM会员，CCF会员；主要承担“计算机组成原理”、“计算机操作系统原理”等课程的理论教学和实践教学；主持、主研国家自然科学基金项目、国家863计划项目、联合国开发计划署（UNDP）项目、四川省科技支撑计划项目、四川省高校成果转换重大培育项目等研究课题10余项；在IEEE Transactions on Communications、IEEE Communications Letters等国内外重要学术期刊和学术会议发表学术论文20余篇，其中SCI核心收录3篇，EI核心收录7篇；申请国家发明专利2项；主要从事射频识别（RFID）技术、物联网（IOT）技术、现代教育技术等领域的科学研究工作。', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('21', null, '/Excellent/pages/images/wangkun.png', '王   昆', '王   昆', '副教授，硕士，西南交通大学计算机应用技术专业毕业。主要从事计算机通信与网络、计算机网络协议仿真和网格划分技术研究。近几年发表论文10余篇， 其中核心期刊6篇，EI检索1篇；教材1本；主持或参与科研项目多项，教育厅项目1项。获得学院多媒体教学比赛中获得一等奖。同时获得学校多媒体教学比赛中获得二等奖。 承担：计算机组成原理，计算机网络，计算机科学技术导论等课程。\r\n（E-mail:  357227403@qq.com）', null, null, null, null, null, null);
INSERT INTO `teacher` VALUES ('22', null, '/Excellent/pages/images/tangjun.png', '唐   峻', '唐   峻', '男，硕士研究生，讲师。主要研究方向：图像处理、计算机视觉。近年来主持校级青年预研基金项目1项，参研项目2项；发表论文5篇。主要承担《软件项目管理》等课程的教学工作。\r\n(Email: tangjun@swust.edu.cn)', null, null, null, null, null, null);
